mapper.select:
select * from borrow


        state = #{state}

    and user_id = #{userId} and book_id = #{bookId}

select borrow.*, book.id, book.name, book.author, book.image from borrow inner join book ON borrow.book_id = book.id


        borrow.state = #{state}

    and borrow.user_id = #{userId}

select * from borrow


        state = #{state}

    and book_id = #{id}
